import { useContext } from "react";

import Modal from "../ui/modal";
import classes from "./cart.module.css";
import CartContext from "../cart/cart-ctx";
import CartItem from "./CartItem";

const Cart = (props) => {
  const cartctx = useContext(CartContext);

  const totalamount = `$${cartctx.totalAmount.toFixed(2)}`;
  const items = cartctx.items.length > 2;

  const cartitemremover = id => {
      cartctx.removeItem(id);
  };

  const cartitemadder = id => {
      cartctx.additem({...items,amount:1});
  };
  return (
    <Modal>
      <ul className={classes["cart-items"]}>
        {cartctx.items.map((item) => (
          <CartItem
            key={item.id}
            name={item.name}
            amount={item.amount}
            price={item.price}
            onRemove={cartitemremover(item.id)}
            onAdd={cartitemadder.bind(null, item)}
          ></CartItem>
        ))}
      </ul>
      <div className={classes.total}>
        <span>Total - </span>
        <span>{totalamount}</span>
      </div>
      <div className={classes.actions}>
        <button className={classes["button--alt"]} onClick={props.onClose}>
          Close
        </button>
        {items && <button className={classes.button}>Order</button>}
      </div>
    </Modal>
  );
};

export default Cart;
