import { useContext, useState } from "react";

import Modal from "../ui/modal";
import classes from "./cart.module.css";
import CartContext from "../cart/cart-ctx";
import CartItem from "./CartItem";
import Checkout from "./checkout";

const Cart = (props) => {
  const [checkout, setcheckout] = useState(false);
  const cartctx = useContext(CartContext);

  const totalamount = `$${cartctx.totalAmount.toFixed(2)}`;
  const items = cartctx.items.length > 2;

  const cartitemremover = id => {
      cartctx.removeItem(id);
  };

  const cartitemadder = item => {
      cartctx.additem(item);
  };

  const orderhandle = ()=> {
    setcheckout(true);
  }
  return (
    <Modal>
      <ul className={classes["cart-items"]}>
        {cartctx.items.map((item) => (
          <CartItem
            key={item.id}
            name={item.name}
            amount={item.amount}
            price={item.price}
            onRemove={cartitemremover.bind(null, item.id)}
            onAdd={cartitemadder.bind(null, item)}
          ></CartItem>
        ))}
      </ul>
      <div className={classes.total}>
        <span>Total - </span>
        <span>{totalamount}</span>
      </div>

      {checkout && <Checkout />}
      {}
      <div className={classes.actions}>
        <button className={classes["button--alt"]} onClick={props.onClose}>
          Close
        </button>
        {items && <button className={classes.button} onClick={orderhandle}>Order</button>}
      </div>
      
    </Modal>
  );
};

export default Cart;
